package com.ksb.myapplication

import android.view.View
import android.widget.ImageView
import android.widget.TextView
import androidx.recyclerview.widget.RecyclerView
import com.bumptech.glide.Glide

class ViewHolder(view: View) : RecyclerView.ViewHolder(view) {

    val vh_name: TextView = view.findViewById(R.id.tv_rv_name)
    val vh_age: TextView = view.findViewById(R.id.tv_rv_age)
    val vh_photo: ImageView = view.findViewById(R.id.img_rv_photo)

    fun bind(data: TodayData){
        vh_name.text=data.name
        vh_age.text=data.age
//        vh_photo = Glide.with(itemView)
//            .load(data.img)
//            .into(vh_photo)

        Glide.with(itemView)
            //.load("data:image/jpeg;base64,")
            .load(data.img)
            .into(vh_photo)

        vh_photo.setClipToOutline(true)
    }
}